mapper.sql:
LEFT JOIN sys_role_user sru ON t.id = sru.userId LEFT JOIN sys_role sr ON sr.id = sru.roleId
and sr.name = #{params.role}
and username like concat("%",#{params.username},"%")

mapper.select:
SELECT t.*, ( SELECT count(1) FROM x_audit_user xau WHERE xau.enable_flag='Y' AND xau.sys_user_id=t.id GROUP BY xau.sys_user_id ) count FROM sys_user t
order by username asc
limit #{offset}, #{limit}

mapper.update:
update x_audit_user t
set sys_user_id = #{acceptId}
where t.sys_user_id = #{handleId} and t.enable_flag = 'Y'
