mapper.sql:
LEFT JOIN sys_role_user sru ON t.id = sru.userId
LEFT JOIN sys_role sr ON sr.id = sru.roleId


        and sr.name = #{params.type}


        and id = #{params.id}


        and sys_user_id = #{params.sysUserId}


        and user_gid = #{params.userGid}


        and create_time = #{params.createTime}


        and update_time = #{params.updateTime}


        and enable_flag = #{params.enableFlag}

mapper.select:
SELECT t.* FROM sys_user t

${params.orderBy}
limit #{offset}, #{limit}
